#include <stdio.h>

int story(int i)
{
    if(i < 50)
        printf("从前有座山,\n");
        for(i=0;i<60000000;i++);  //delay
        printf("山里有做庙,\n");
        for(i=0;i<60000000;i++);
        printf("庙里有个老和尚,\n");
        for(i=0;i<60000000;i++);
        printf("老和尚在给小和尚讲故事,\n");
        for(i=0;i<60000000;i++);
        printf("讲的啥？\n");
        for(i=0;i<90000000;i++);
        printf("\n\n");

        i++;
        story(i);
    return i;
}

int main(void)
{
    printf("开始讲故事啦\n\n");
    story(0);
    return 0;
}




