#include <stdio.h>

int main(void)
{
    int array[3][3][3] = {  // 定义一个三行三列的数组
        {
            {1, 2, 3},
            {1, 2, 3},
            {1, 2, 3}
        },
        {
            {1, 2, 3},
            {1, 2, 3},
            {1, 2, 3}
        },
        {
            {1, 2, 3},
            {1, 2, 3},
            {1, 2, 3}
        }
    };

    for(int i = 0; i<3; i++) {
        for(int j = 0; j<3; j++) {
            for(int k = 0; k<3; k++) {
                printf("%d ",array[i][j][k]);
            }
            printf("\n");
        }
        printf("\n");
    }
    return 0;
}




